    <style id='orderguard-phone-validation-styles-inline-css'>
        .woocommerce-error.orderguard-phone-error {
            position: relative;
            padding: 15px 20px 15px 45px;
            margin: 0 0 20px;
            border-radius: 8px;
            background: linear-gradient(135deg, #ff6b6b 0%, #ff4444 100%);
            color: #ffffff;
            border: none;
            box-shadow: 0 4px 15px rgba(255, 68, 68, 0.2);
            transform-origin: top;
            animation: slideInDown 0.5s ease-out, shake 0.8s cubic-bezier(.36, .07, .19, .97) both;
        }

        .woocommerce-error.orderguard-phone-error::before {
            content: '\f534';
            font-family: dashicons;
            display: inline-block;
            position: absolute;
            top: 50%;
            left: 15px;
            transform: translateY(-50%);
            font-size: 20px;
            color: #ffffff;
            animation: pulse 2s infinite;
        }

        .woocommerce-error.orderguard-phone-error li {
            padding: 0;
            margin: 0;
            color: #ffffff;
            font-size: 14px;
            font-weight: 500;
            line-height: 1.5;
        }

        @keyframes slideInDown {
            from {
                transform: translateY(-20px);
                opacity: 0;
            }

            to {
                transform: translateY(0);
                opacity: 1;
            }
        }

        @keyframes shake {

            10%,
            90% {
                transform: translateX(-1px);
            }

            20%,
            80% {
                transform: translateX(2px);
            }

            30%,
            50%,
            70% {
                transform: translateX(-4px);
            }

            40%,
            60% {
                transform: translateX(4px);
            }
        }

        @keyframes pulse {
            0% {
                transform: translateY(-50%) scale(1);
            }

            50% {
                transform: translateY(-50%) scale(1.2);
            }

            100% {
                transform: translateY(-50%) scale(1);
            }
        }

        /* Input field validation styles */
        #billing_phone.orderguard-invalid {
            border-color: #ff4444 !important;
            box-shadow: 0 0 0 1px #ff4444 !important;
            animation: shake 0.8s cubic-bezier(.36, .07, .19, .97) both;
        }

        #billing_phone:focus {
            transition: all 0.3s ease;
        }

        /* Live validation message */
        .orderguard-live-validation {
            display: none;
            font-size: 12px;
            color: #ff4444;
            margin-top: 5px;
            padding: 8px 12px;
            border-radius: 4px;
            background-color: rgba(255, 68, 68, 0.1);
            animation: fadeIn 0.3s ease-out;
        }

        @keyframes fadeIn {
            from {
                opacity: 0;
                transform: translateY(-10px);
            }

            to {
                opacity: 1;
                transform: translateY(0);
            }
        }
    </style>
